package metodosString;

public class MetodoTrimm {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		
		String hola="  hola  ";
		
		System.out.println(hola); // devuelve "  hola  " con los espacios
		System.out.println(hola.trim()); // devuelve "hola" , elimina los espacios iniciales y finales
	}

}
